'use strict';

function beep() {
    var snd = new Audio("data:audio/wav;base64,");  
    snd.play();
}

var mass = [];
var massPlus = [];

var txt_stl = 'font-size: 13px;';
var style = 'color: red; font-size: 15px;';
var style2 = 'color: lime;';
var style3 = 'color: yellow; font-size: 14px;';

var dice = document.querySelector('.index__home__indicator__inner__number__roll > span');
var parentElem = document.querySelector('.sc-hRmvpr.gZRIjj');
var elem = document.createElement('div');
elem.className = "sc-ESoVU iKOMAn";
parentElem.appendChild(elem);

var numbers = [];

var timer, betAmount, startBet;

function start(maxStreak, stopBetting = false) {
  betAmount = document.querySelector('.sc-hwwEjo.jqykZk');
  startBet = betAmount.getAttribute('value');
  var numberStrategy = document.querySelector('.sc-cBdUnI.dzvZuC');
  var strategy = Number(numberStrategy.innerHTML);
  numbers[numbers.length] = Number(dice.innerHTML);
  if (strategy > 50) {
    timer = setInterval(function(){
      var rollingNumber = Number(dice.innerHTML);
      console.log(' ');
      if (rollingNumber != numbers[numbers.length-1]) {
        numbers[numbers.length] = rollingNumber;
        if (rollingNumber < strategy) {
          massPlus = [];
          mass[mass.length] = rollingNumber;
          elem.innerHTML = "<p>Streak</p><strong style=\"color: red;\">" + mass.length + "</strong>";
          if (mass.length == maxStreak) {
            document.querySelector('.sc-eAKXzc.jaNoVu.sc-cHGsZl.bRPrEn').click();
            elem.innerHTML = "<p>Streak</p><strong style=\"color: rgb(255, 255, 255);\">" + mass.length + "!</strong>";
            beep();
          }
        } else {
          mass = [];
          massPlus[massPlus.length] = rollingNumber;
          elem.innerHTML = "<p>Streak</p><strong style=\"color: lime;\">" + massPlus.length + "</strong>";
          if (stopBetting) {
            document.querySelector('.sc-eAKXzc.jaNoVu.sc-cHGsZl.bRPrEn').click();
          }
        }
      }
    }, 200);
  } else {
    timer = setInterval(function(){
      var rollingNumber = Number(dice.innerHTML);
      if (rollingNumber != numbers[numbers.length-1]) {
        numbers[numbers.length] = rollingNumber;
        if (rollingNumber > strategy) {   
          massPlus = [];
          mass[mass.length] = rollingNumber;
          elem.innerHTML = "<p>Streak</p><strong style=\"color: rgb(255, 255, 255);\">" + mass.length + "</strong>";
          if (mass.length == maxStreak) {
            document.querySelector('.sc-eAKXzc.jaNoVu.sc-cHGsZl.bRPrEn').click();
            elem.innerHTML = "<p>Streak</p><strong style=\"color: rgb(255, 255, 255);\">" + mass.length + "!</strong>";
            beep();
          }
        } else {
          mass = [];
          massPlus[massPlus.length] = rollingNumber;
          elem.innerHTML = "<p>Streak</p><strong style=\"color: lime;\">" + massPlus.length + "</strong>";
          if (stopBetting) {
            document.querySelector('.sc-eAKXzc.jaNoVu.sc-cHGsZl.bRPrEn').click();
          }
        }
      }
    }, 200);
  }
}

function stop() {
  clearInterval(timer);
  mass = [];
  massPlus = [];
  numbers = [];
}

var streak;
window.onkeydown = function(event) {
  if (event.altKey && event.keyCode === 84) {
    alert("Counter Started!");
    start(10000, true);
    console.log("Counter Started!");
    console.log("Max lose streak was installed!\nCurrent max streak: " + streak);
  } else if (event.altKey && event.keyCode === 89) {
    stop();
    alert("Counter Stopped!");
    console.log("Counter Stopped!");
  }
};
